// src/ImageUpload.js
import React, { useState } from 'react';
import axios from 'axios';
import './ImageUpload.css'; // Import CSS for styling

const ImageUpload = () => {
    const [selectedImage, setSelectedImage] = useState(null);
    const [responseText, setResponseText] = useState('');

    const handleImageChange = (event) => {
        setSelectedImage(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!selectedImage) return;

        const formData = new FormData();
        formData.append('file', selectedImage);

        try {
            const response = await axios.post('http://127.0.0.1:8000/upload-photo', formData);
            setResponseText(response.data.description);
        } catch (error) {
            console.error('Error uploading image:', error);
            setResponseText('Error uploading image');
        }
    };

    return (
        <div className="upload-container">
            <input type="file" onChange={handleImageChange} />
            <button onClick={handleUpload}>Upload</button>
            {selectedImage && (
                <div className="image-preview">
                    <img src={URL.createObjectURL(selectedImage)} alt="Preview" />
                </div>
            )}
            {responseText && (
                <div className={`description-box ${responseText ? 'visible' : ''}`}>
                    {responseText}
                </div>
            )}
        </div>
    );
};

export default ImageUpload;
